import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { useHistory} from 'react-router-dom';
import '../../global.css';
import logo from '../../assets/logo.png';
import api from '../../services/api';


export default function Doacoes(){

    const [doacoes, setDoacoes] = useState([]);
    const [page, setPage] = useState(1);

    const nome = localStorage.getItem('nome');
    const accessToken = localStorage.getItem('accessToken');

    const history = useHistory();


    async function editarDoacao(id) {
        try {
            await api.delete(`doacoes/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })
        } catch (error) {
            alert('Edit failed! Try again.');
        }
    }
    async function deleteDoacao(id) {
        try {
            await api.delete(`doacoes/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })

            setDoacoes(doacoes.filter(doacao => doacao.id !== id))
        } catch (err) {
            alert('Delete failed! Try again.');
        }
    }

    async function fetchMoreBooks() {
        const response = await api.get('doacoes', {
            headers: {
                Authorization: `Bearer ${accessToken}`
            },
            params: {
                pageNumber: page,
                pageSize: Number,
                limit: 4,
                direction: 'asc'
            }
        });

        setDoacoes([ ...doacoes, ...response.data])
        setPage(page + 1);
    }

    useEffect(() => {
        fetchMoreBooks();
    }, [])




    return (

        <body>
            <header>
                <Link to = {"/"}><img src = {logo} id = "logo"/></Link>

                <ul>
                    <li id="pq-doar"><Link to = {"/porqueDoar"}> PORQUE DOAR? </Link> </li>
                    <li id="view-donate"><Link to = {"/doacoes"}> VER DOAÇÕES </Link> </li>
                    <li id="donate-now"><Link to = {"/cadastrarDoacao"}> DOAR JÁ </Link> </li>
                </ul>

            </header>

            <h1>INICIAR DOAÇÃO</h1>
            <h2>INCIE HOJE UMA NOVA DOAÇÃO</h2>

            <ul>
                {doacoes.map(doacao => 
                    <li key = {doacao.id}>
                        <strong>Tipo de Doação:</strong>
                        <p>{doacao.produto}</p>
                        <strong>Telefone:</strong>
                        <p>{doacao.valor}</p>
                        <strong>Valor:</strong>
                        <p>{doacao.telefone}</p>
                        <strong>Descrição:</strong>
                        <p>{doacao.descricao}</p>
                        <button onClick={() => editarDoacao(doacoes.id)} type="button"/>

                        <button onClick={() => deleteDoacao(doacoes.id)} type="button"/>


                    </li>
                )}
            </ul>

            <input id="btn-criar" type="button" placeholder = "Proxima pagina" />


        </body>


    );
}
