import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { useHistory} from 'react-router-dom';
import '../../global.css';
import logo from '../../../assets/logo.png';
import api from '../../services/api.js';


export default function Login(){
    const [cpfouCnpj, setCpfOucnpj] = useState('');
    const [password, setPassword] = useState('');

    const history = useHistory();

    async function login(e){
        e.preventDefault();

        const data = {
            cpfouCnpj,
            password,
        };

        try {
            const response = await api.post('/Login', data);

            localStorage.setItem('cpfouCnpj', cpfouCnpj);
            localStorage.setItem('accessToken', response.data.token);

            history.push('/doacoes')
        } catch (err) {
            alert('Login failed! Try again!');
        }
    };

    return (
        <body>
            <header>
                <Link to = {"/Login"}><img src = {logo} id = "logo"/></Link>

                <ul>
                    <li id="pq-doar"><Link to = {"/porqueDoar"}> PORQUE DOAR? </Link> </li>
                    <li id="view-donate"><Link to = {"/doacoes"}> VER DOAÇÕES </Link> </li>
                    <li id="donate-now"><Link to = {"/cadastrarDoacao"}> DOAR JÁ </Link> </li>
                </ul>


            </header>

        <h1>LOGIN</h1>
        <p>É UM PRAZER TER VOCÊ AQUI!</p>



    <main>  
        <section>
        <div id = "login">
            <p>Já tenho uma conta</p>

            <form onSubmit = {login}>
               <input id="user" type="text" placeholder="CPF/CNPJ"
                        value={cpfouCnpj}
                        onChange={e => setCpfOucnpj(e.target.value)}
               />

                <input id="senha" type="password" name = "senha" placeholder="Senha"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                />

                <p id="esqueceu_senha">Esqueci minha senha</p>

                <button id="entrar-btn" type="submit">ENTRAR</button>
            </form>
        </div>

        <div id ="cadastro">
            <p>Cadastre-se aqui</p>
            <button id="criar-btn" value="CRIE UMA CONTA"><Link to = {"auth/singup"}> CADASTRE-SE AQUI</Link></button>
        </div>


        </section>

    </main>
        <script src="https://unpkg.com/ionicons@4.5.10-0/dist/ionicons.js" ></script>
     </body>
    )
}
